import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { saveResult } from '../services/game';

const initialSudoku = [
  [5, 3, 0, 0, 7, 0, 0, 0, 0],
  [6, 0, 0, 1, 9, 5, 0, 0, 0],
  [0, 9, 8, 0, 0, 0, 0, 6, 0],
  [8, 0, 0, 0, 6, 0, 0, 0, 3],
  [4, 0, 0, 8, 0, 3, 0, 0, 1],
  [7, 0, 0, 0, 2, 0, 0, 0, 6],
  [0, 6, 0, 0, 0, 0, 2, 8, 0],
  [0, 0, 0, 4, 1, 9, 0, 0, 5],
  [0, 0, 0, 0, 8, 0, 0, 7, 9]
];



const Game = () => {
  const { user } = useAuth();
  const [sudoku, setSudoku] = useState(initialSudoku);
  const [attempts, setAttempts] = useState(3);
  const [timer, setTimer] = useState(0);
  const [isIncorrect, setIsIncorrect] = useState(false);
  const navigate = useNavigate();
  const inputRefs = useRef([]);

  useEffect(() => {
    const interval = setInterval(() => {
      setTimer(timer => timer + 1);
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const handleChange = (row, col, value) => {
    if (attempts <= 0) return;
    const newSudoku = [...sudoku];
    newSudoku[row][col] = value;
    setSudoku(newSudoku);
  };

  const handleKeyDown = (e, row, col) => {
    if (attempts <= 0) return;

    switch (e.key) {
      case 'ArrowUp':
        if (row > 0) inputRefs.current[(row - 1) * 9 + col].focus();
        break;
      case 'ArrowDown':
        if (row < 8) inputRefs.current[(row + 1) * 9 + col].focus();
        break;
      case 'ArrowLeft':
        if (col > 0) inputRefs.current[row * 9 + (col - 1)].focus();
        break;
      case 'ArrowRight':
        if (col < 8) inputRefs.current[row * 9 + (col + 1)].focus();
        break;
      default:
        break;
    }
  };

  const handleSubmit = async () => {
    if (attempts > 0) {
      const isCorrect = checkSudoku(sudoku);
      if (isCorrect) {
        alert('Parabéns, você completou o Sudoku!');
        await saveResult({ username: user.username, timeTaken: timer });
        navigate('/ranking');
      } else {
        setIsIncorrect(true);
        setTimeout(() => setIsIncorrect(false), 500);
        setAttempts(attempts - 1);
        if (attempts === 1) {
          alert('Acabaram as tentativas.');
          navigate('/');
        }
      }
    }
  };

  const checkSudoku = (sudoku) => {
    // Verificar linhas
    for (let row = 0; row < 9; row++) {
      const rowSet = new Set();
      for (let col = 0; col < 9; col++) {
        if (sudoku[row][col] === 0 || rowSet.has(sudoku[row][col])) {
          return false;
        }
        rowSet.add(sudoku[row][col]);
      }
    }

    // Verificar colunas
    for (let col = 0; col < 9; col++) {
      const colSet = new Set();
      for (let row = 0; row < 9; row++) {
        if (sudoku[row][col] === 0 || colSet.has(sudoku[row][col])) {
          return false;
        }
        colSet.add(sudoku[row][col]);
      }
    }

    return true;
  };

  return (
    <div>
      <h2>Sudoku</h2>
      <div>Tempo: {timer}s</div>
      <div>Tentativas restantes: {attempts}</div>
      <div className={isIncorrect ? 'sudoku incorrect' : 'sudoku'}>
        {sudoku.map((row, rowIndex) => (
          <div key={rowIndex} className="sudoku-row">
            {row.map((cell, colIndex) => (
              <input
                key={colIndex}
                type="number"
                min="1"
                max="9"
                value={cell !== 0 ? cell : ''}
                onChange={(e) => handleChange(rowIndex, colIndex, Number(e.target.value))}
                onKeyDown={(e) => handleKeyDown(e, rowIndex, colIndex)}
                className="sudoku-cell"
                ref={el => inputRefs.current[rowIndex * 9 + colIndex] = el}
              />
            ))}
          </div>
        ))}
      </div>
      <button onClick={handleSubmit}>Fazer Tentativa</button>
    </div>
  );
};

export default Game;
