import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const initialSudoku = [
  [5, 3, 0, 0, 7, 0, 0, 0, 0],
  [6, 0, 0, 1, 9, 5, 0, 0, 0],
  [0, 9, 8, 0, 0, 0, 0, 6, 0],
  [8, 0, 0, 0, 6, 0, 0, 0, 3],
  [4, 0, 0, 8, 0, 3, 0, 0, 1],
  [7, 0, 0, 0, 2, 0, 0, 0, 6],
  [0, 6, 0, 0, 0, 0, 2, 8, 0],
  [0, 0, 0, 4, 1, 9, 0, 0, 5],
  [0, 0, 0, 0, 8, 0, 0, 7, 9]
];

const Game = () => {
  const [sudoku, setSudoku] = useState(initialSudoku);
  const [attempts, setAttempts] = useState(3);
  const [timer, setTimer] = useState(0);
  const [isIncorrect, setIsIncorrect] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const interval = setInterval(() => {
      setTimer(timer => timer + 1);
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const handleChange = (row, col, value) => {
    if (attempts <= 0) return;
    const newSudoku = [...sudoku];
    newSudoku[row][col] = value;
    setSudoku(newSudoku);
  };

  const handleSubmit = () => {
    if (attempts > 0) {
      const isCorrect = checkSudoku(sudoku);
      if (isCorrect) {
        alert('Parabéns, você completou o Sudoku!');
        // Lógica para salvar o resultado pode ser adicionada aqui
      } else {
        setIsIncorrect(true);
        setTimeout(() => setIsIncorrect(false), 500);
        setAttempts(attempts - 1);
        if (attempts === 1) {
          alert('Acabaram as tentativas.');
          navigate('/');
        }
      }
    }
  };

  const checkSudoku = (sudoku) => {
    // Lógica para verificar se o Sudoku está correto
    // Por simplicidade, vamos assumir que sempre está incorreto para o exemplo
    return false;
  };

  return (
    <div>
      <h2>Sudoku</h2>
      <div>Tempo: {timer}s</div>
      <div>Tentativas restantes: {attempts}</div>
      <div className={isIncorrect ? 'sudoku incorrect' : 'sudoku'}>
        {sudoku.map((row, rowIndex) => (
          <div key={rowIndex}>
            {row.map((cell, colIndex) => (
              <input
                key={colIndex}
                type="number"
                min="0"
                max="9"
                value={cell !== 0 ? cell : ''}
                onChange={(e) => handleChange(rowIndex, colIndex, Number(e.target.value))}
                disabled={cell !== 0}
                style={{ width: '40px', height: '40px', textAlign: 'center' }}
              />
            ))}
          </div>
        ))}
      </div>
      <button onClick={handleSubmit}>Fazer Tentativa</button>
    </div>
  );
};

export default Game;
